mapper.update:
	UPDATE BOARD SET
    BOARD_NO = #{boardNum}
    UPDATE_DATE = CURRENT_TIMESTAMP
    BOARD_CONTENT = #{boardContent}
    BOARD_TITLE = #{boardTitle}
    LINK = #{link}

	SELECT MAX(BOARD_NO) +1 FROM BOARD

mapper.select:
SELECT UDB05 
FROM BOARD 
WHERE USER_ID= #{userId}

SELECT UDB04 
FROM BOARD 
WHERE USER_ID= #{userId}
