mapper.select:
   	SELECT * FROM BOARD 
	WHERE BOARD_NO = #{boardNo}
	AND USER_ID = #{userId}

mapper.update:
UPDATE BOARD SET
BOARD_NO = #{boardNo},
UPDATE_DATE = CURRENT_TIMESTAMP,
BOARD_CONTENT = #{boardContent},
BOARD_TITLE = #{boardTitle},
LINK = #{link}
WHERE BOARD_NO = #{boardNo}
AND USER_ID = #{userId}	

	SELECT MAX(BOARD_NO) +1 FROM BOARD
