mapper.select:
SELECT UT.USER_NAME, UR.USER_ID, UT.USER_EMAIL, UR.RESUME_TITLE,
UR.RESUME_NO,
UR.JOB_TYPE, UR.CAREER, UR.GENDER, UR.EDUCATION
FROM USER_RESUME UR
JOIN USER_TABLE UT ON UR.USER_ID = UT.USER_ID
WHERE RESUME_ACT = 'Y'

	AND JOB_TYPE = #{jobType}


	AND CAREER = #{career}


	AND EDUCATION = #{education}


	AND GENDER = #{gender}

SELECT COUNT(RESUME_NO)
FROM ( SELECT UT.USER_NAME, UR.USER_ID, UT.USER_EMAIL, UR.RESUME_TITLE,UR.RESUME_NO, UR.JOB_TYPE, UR.CAREER, UR.GENDER, UR.EDUCATION
	  		  FROM USER_RESUME UR
	  	      JOIN USER_TABLE UT ON UR.USER_ID = UT.USER_ID
	  	     WHERE RESUME_ACT = 'Y' 
			AND JOB_TYPE = #{jobType} 

			AND CAREER = #{career} 

			AND EDUCATION = #{education} 

			AND GENDER = #{gender} 
	 )

SELECT USER_NAME, USER_ID, USER_EMAIL, RESUME_TITLE, RESUME_NO, JOB_TYPE, CAREER, GENDER, EDUCATION
  FROM (SELECT ROWNUM AS NUM , A.* 
		  FROM (SELECT UT.USER_NAME, UR.USER_ID, UT.USER_EMAIL, UR.RESUME_TITLE,UR.RESUME_NO, UR.JOB_TYPE, UR.CAREER, UR.GENDER, UR.EDUCATION
		  		  FROM USER_RESUME UR
		  	      JOIN USER_TABLE UT ON UR.USER_ID = UT.USER_ID
		  	     WHERE RESUME_ACT = 'Y' 
				AND JOB_TYPE = #{jobType} 

				AND CAREER = #{career} 

				AND EDUCATION = #{education} 

				AND GENDER = #{gender} 
				ORDER BY UR.RESUME_NO DESC
				) A
		)
	WHERE NUM BETWEEN #{startNum} AND #{endNum}

mapper.update:
UPDATE USER_TABLE SET
USER_PW = #{userPw}
WHERE USER_ID
= #{userId}

UPDATE tb_user SET
USER_PIC = #{userPic}
WHERE user_id
= #{userId}

mapper.select:
SELECT RA_NUM, RECRUIT_TYPE,USER_ID,COMPANY_NAME , CAREER_TYPE, USER_EDUCATION, SALARY, REGIST_DATE , CLOSE_DATE, RA_TITLE, RA_CONTENT,RA_EXTRA_DOCUMENT , CAREER, HOLIDAY_TYPE, RA_ADMISSION FROM( SELECT ROWNUM AS NUM , A.* FROM ( SELECT RA_NUM, RECRUIT_TYPE,USER_ID,COMPANY_NAME, CAREER_TYPE , USER_EDUCATION, SALARY, REGIST_DATE, CLOSE_DATE , RA_TITLE, RA_CONTENT, RA_EXTRA_DOCUMENT , CAREER, HOLIDAY_TYPE, RA_ADMISSION FROM RECRUIT_REGIST WHERE USER_ID = #{userId}
AND RA_TITLE like '%'||#{search}||'%' or RA_CONTENT like '%'||#{search}||'%' or COMPANY_NAME like '%'||#{search}||'%'
ORDER BY RA_NUM DESC ) A ) WHERE NUM BETWEEN #{startNum} AND #{endNum}

SELECT RA_NUM,COMPANY_NAME,RA_TITLE
FROM RECRUIT_REGIST
WHERE USER_ID = #{userId}

mapper.update:
UPDATE RECRUIT_REGIST SET COMPANY_NAME =
#{companyName}, RECRUIT_TYPE =#{recruitType}
,
USER_EDUCATION=#{userEducation},CONDITION_TYPE=#{conditionType},
SALARY= #{salary}, MIN_SALARY=#{minSalary}
, MAX_SALARY=#{maxSalary},
REGIST_DATE= #{registDate},CLOSE_DATE=#{closeDate}
, CAREER=#{career},
HOLIDAY_TYPE=#{holidayType}, EMP_TYPE_CODE=#{empTypeCode}
,
RA_TITLE=#{raTitle}, RA_CONTENT=#{raContent}
,
RA_EXTRA_DOCUMENT=#{raExtraDocument}
WHERE RA_NUM = #{raNum}
AND USER_ID
= #{userId}

mapper.select:
		SELECT *
		FROM RECRUIT_REGIST r
		WHERE CLOSE_DATE > SYSDATE
		AND RA_ADMISSION = 'Y'

		  AND (


		      r.COMPANY_NAME LIKE '%' || #{k} || '%'
		      OR r.RA_TITLE LIKE '%' || #{k} || '%'
		      OR r.RA_CONTENT LIKE '%' || #{k} || '%'


		  )


        AND (
        r.USER_EDUCATION IN 

            #{userEducation}



	        OR r.CAREER IN 

	            #{career}



	        OR r.CONDITION_TYPE IN 

	            #{conditionType}



	        OR r.EMP_TYPE_CODE IN 

	            #{empTypeCode}



