mapper.select:
  	select USER_ID 
  	from USER_TABLE 
  	WHERE USER_NAME = #{userName} AND USER_BIRTH = #{userBirth}
	  	
	  		
	  			AND USER_EMAIL = #{userEmail}
	  		
	  		
	  			AND USER_PHONE = #{userPhone}

  	select USER_ID, USER_EMAIL, USER_PHONE, TERMS_PERIOD
  	from USER_TABLE 
  	WHERE USER_ID =  #{userId} AND USER_NAME = #{userName} AND USER_BIRTH = #{userBirth}
	  	
	  		
	  			AND USER_EMAIL = #{userEmail}
	  		
	  		
	  			AND USER_PHONE = #{userPhone}

mapper.update:
update USER_TABLE set 
USER_PIC=#{userPic}
where USER_ID = #{userId}

mapper.delete:
DELETE FROM
SCRAP_INFO
WHERE USER_ID = #{userId} AND RA_NUM = #{raNum}

	DELETE FROM
  	BUSINESS_APPLICANT
  	WHERE USER_ID = #{userId} AND RA_NUM = #{raNum}
