mapper.select:
select RESUME_NO,
	RESUME_TITLE, RESUME_PHOTO, RESUME_DATE, PORTF_FILE FROM
	USER_RESUME
WHERE USER_ID = #{USER_ID} 
AND RESUME_DATE = (
					SELECT MAX(RESUME_DATE)
					FROM USER_RESUME
					WHERE USER_ID = #{USER_ID}
					)

mapper.update:
UPDATE RESUME_CERTI SET
CERTI_NAME = #{certiName},
CERTI_PUB = #{certiPub},
CERTI_DATE = #{certiDate}
WHERE CERTI_NO = #{certiNo}

UPDATE RESUME_CL SET
CL_GROWTH = #{clGrowth},
CL_MOTIVE = #{clMotive},
CL_ADV = #{clAdv},
CL_ASP = #{clAsp}

	, CL_FILE = #{clFile}

WHERE CL_NO = #{clNo}
